import React from "react";
import NavBar from "./NavBar";
import {Row} from 'reactstrap';
import Leftcomponent from "./Leftcomponent";
import Footer from "./Footer";
import Rightcomponent from "./Rightcomponent";
import base from "./base";
import {firebaseApp} from "./base";
class App extends React.Component{
    state={
        editor:{},
        isopen:false,
        savefiles:{},
        mode:"none",
        dropdownvalue:""
    }
    
    componentDidMount(){
        this.ref= base.syncState("/tulsisharma/savefiles",{
            context:this,
            state:'savefiles'
        });
        setTimeout(() => {
            this.updateeditor();
        },3200);
    }

    componentWillUnmount(){
        // base.removeBinding(this.ref);
    }

    setdropdown=(val)=>{
        this.setState({dropdownvalue:val});
    }

    updateeditor=()=>{
        const id = this.props.match.params.id;
        if(id && this.state.savefiles[id]){
            const data = this.state.savefiles[id];
            this.setdropdown(data.mode);
            this.state.editor.setValue(data.content);
            switch (data.mode) {
                case "C++":
                    this.state.editor.setOption("mode","text/x-c++src");
                    break;
                case "C":
                    this.state.editor.setOption("mode","text/x-csrc");
                    break;
            }
            if(this.state.dropdownvalue===data.mode){
                this.state.editor.on('change',function(){
                    data.content=this.state.editor.getValue();
                    this.updatevalue(id,data);
                }); 
            }
        }
    }

    updatevalue=(key,val)=>{
        const savefiles={...this.state.savefiles};
        savefiles[key]=val;
        this.setState({savefiles:savefiles});
    }

    setmode=(val)=>{
        this.setState({mode:val});
    }
    savefile=(val)=>{
        const data={
            name:val,
            content:this.state.editor.getValue(),
            status:"saved",
            mode:this.state.mode
        }
        const savefiles ={...this.state.savefiles};
        savefiles[`file${Date.now()}`]=data;
        this.setState({savefiles:savefiles});
    }
    seteditor=(val)=>{
        this.setState({editor:val});
    }
    settheme=(val)=>{
        this.state.editor.setOption("theme",val);
    }
    settab=(val)=>{
        this.state.editor.setOption("indentUnit",val);
    }
    setsize=(val)=>{
        this.state.editor.getWrapperElement().style["font-size"]= val+"px";
        this.state.editor.refresh();
    }
    toggle=()=>{
        this.setState({isopen:!this.state.isopen});   
    }
    render(){
        return(
            <div className="container-fluid" style={{paddingLeft:"0px",paddingRight:"0px"}}>
                <div className="main-wrapper">
                    <NavBar settheme={this.settheme} settab={this.settab} setsize={this.setsize} isopen={this.state.isopen} savefile={this.savefile} toggle={this.toggle}/>
                    <div className="gap"></div>
                    <div className="" style={{marginLeft:"20px",marginRight:"20px"}}>
                        <Row>
                            <Leftcomponent editor={this.state.editor} seteditor={this.seteditor} toggle={this.toggle} setmode={this.setmode} setdropdown={this.setdropdown} dropdownvalue={this.state.dropdownvalue}/>
                            <Rightcomponent/>
                        </Row>
                    </div>
                    <br></br>
                    <Footer/>
                </div>
            </div>
        );
    }
}

export default App;
