import React from "react";
import { Row,Col,Button} from "reactstrap";
import { ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';


class Editor extends React.Component {
    state={
        dropdownOpen:false
    }
    inputRef=React.createRef();

    toggle=()=>{
        this.setState({dropdownOpen:!this.state.dropdownOpen});
    }
    render(){
        return(
            <Row>
                <Col lg="12" md="12" sm="12" xs="12">
                    <div className="editor">
                        <div className="editor-nav">
                        <ButtonDropdown isOpen={this.state.dropdownOpen} toggle={this.toggle} style={{marginLeft:"5px"}} ref={inputRef} >
                            <DropdownToggle caret>
                                Button Dropdown
                            </DropdownToggle>
                            <DropdownMenu style={{width:"200px"}}>
                                <DropdownItem selected style={{marginRight:"0px"}}>Action</DropdownItem>
                                <DropdownItem style={{marginRight:"0px"}}>Another Action</DropdownItem>
                            </DropdownMenu>
                        </ButtonDropdown>
                        <Button  size="sm"><i class="fas fa-compress" style={{fontSize:"19px"}}></i></Button>
                        <Button  size="sm"><i class="fas fa-share-alt" style={{fontSize:"19px"}}></i></Button>
                        <Button size="sm"><i class="fas fa-save" style={{fontSize:"19px"}}></i></Button>
                                
                                
                            
                        </div>
                        {/* <div className="vertical-num">
                            
                        </div> */}
                        <textarea style={{height:"400px",width:"100%",border:"none"}} className="textbox">
                        
                        </textarea>
                    </div>
                </Col>
            </Row>
        );
    }
}
export default Editor;
