import React from "react";
import {Button,Collapse} from "reactstrap";
class Modebutton extends React.Component{
    state={
        isOpen:false
    }
    toggle=()=>{
        this.setState({isOpen:!this.state.isOpen});
    }
    render(){
        return(
            <div className="right-wrapper">
                <Button color="secondary" size="md" block onClick={this.toggle} style={{ marginBottom: '1rem' }}>Block level button</Button>
                <Collapse isOpen={this.state.isOpen}>
                    
                </Collapse>
                <Button color="secondary" size="md" block>Block level button</Button>
                <Button color="secondary" size="md" block>Block level button</Button>
            </div>
        );
    }
}
export default Modebutton;
