import React from "react";
import { Row,Col,Button} from "reactstrap";
import { ButtonDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import CodeMirror from "codemirror";
import {cpp,c} from "../data";
import "codemirror/addon/edit/closebrackets.js";
import "codemirror/addon/edit/matchbrackets.js";
import "codemirror/addon/hint/anyword-hint.js";
import "codemirror/addon/hint/show-hint.js";
import "codemirror/addon/hint/show-hint.css";
import "codemirror/addon/selection/active-line.js";
import "codemirror/addon/scroll/annotatescrollbar.js";
import "codemirror/addon/fold/foldcode.js";
import "codemirror/addon/fold/foldgutter.js";
import "codemirror/addon/fold/foldgutter.css";
import "codemirror/addon/fold/brace-fold.js";
import "codemirror/addon/fold/indent-fold.js";
import "codemirror/mode/javascript/javascript.js";
import "codemirror/theme/dracula.css";
import "codemirror/addon/display/autorefresh.js";
import "codemirror/addon/display/placeholder.js";
import "codemirror/mode/clike/clike.js";
import "codemirror/theme/monokai.css";
import "codemirror/theme/ayu-dark.css";
import "codemirror/theme/base16-light.css";
import "codemirror/theme/blackboard.css";
class Editor extends React.Component {
    InputRef=React.createRef();
    state={
        dropdownOpen:false,
        dropdownvalue:"",
    }
    // readfile=async()=>{
    //     await fetch(text, {mode: 'no-cors'})
    //     .then(response => response.text())
    //     .then(async(data) =>{
    //         console.log("enter");
    //         await this.setvalue(data);
    //     }
    //         )
    //     .catch(error => console.error(error));
    //     console.log("hellow");
    // }
    createcodemirror=()=>{
        CodeMirror.commands.autocomplete=(cm)=>{
            cm.showHint({hint:CodeMirror.hint.anyword});
        }
        const editor = CodeMirror.fromTextArea(this.InputRef.current,{
            lineNumbers: true,
            autoFocus:true,
            autoCloseBrackets: true,
            matchBrackets:true,
            styleActiveLine:true,
            lineWrapping:true,
            undoDepth:300,
            extraKeys: {"Ctrl-Q": function(cm){ cm.foldCode(cm.getCursor()); },
                "Ctrl-Space":"autocomplete"
            },
            foldGutter:true,
            gutters: ["CodeMirror-linenumbers", "CodeMirror-foldgutter","error"],
            autoRefresh:true,
            theme:"blackboard",
            electricChars:true
        });
        editor.on("change",()=>{
            console.log(editor.getValue());
        });
        editor.setSize("","550");
        editor.setValue("please select your language first..");
        this.props.seteditor(editor);
        editor.getWrapperElement().style["fontStyle"]= "28px";
        editor.refresh();
    }
    componentDidMount(){
       this.createcodemirror();
    }
    toggle=()=>{
        this.setState({dropdownOpen:!this.state.dropdownOpen});
    }
    updatevalue=(event)=>{
        switch (event.currentTarget.textContent) {
            case "C++":
                this.props.editor.setOption("mode","text/x-c++src");
                this.props.editor.setValue(cpp);
                break;
            case "C":
                this.props.editor.setOption("mode","text/x-csrc");
                this.props.editor.setValue(c);
                break;
            default:
                break;
        }
        this.setState({dropdownvalue: event.currentTarget.textContent}); 
    }
    render(){
        return(
            <Row>
                <Col lg="12" md="12" sm="12" xs="12">
                    <div className="editor">
                        <div className="editor-nav">
                        <ButtonDropdown isOpen={this.state.dropdownOpen} toggle={this.toggle} style={{marginLeft:"5px",height:"100%"}}>
                            <DropdownToggle caret size="sm"style={{backgroundColor:"whitesmoke",color:"black",border:"block"}}>
                                {!this.state.dropdownvalue?"Language":this.state.dropdownvalue}
                            </DropdownToggle>
                            <DropdownMenu style={{width:"200px"}}>
                                <DropdownItem active style={{marginRight:"0px"}} onClick={this.updatevalue} dropdownvalue="click">C++</DropdownItem>
                                <DropdownItem style={{marginRight:"0px"}} onClick={this.updatevalue}>C</DropdownItem>
                            </DropdownMenu> 
                        </ButtonDropdown>
                        <Button  size="sm"><i className="fas fa-compress" style={{fontSize:"19px"}}></i></Button>
                        <Button  size="sm"><i className="fas fa-share-alt" style={{fontSize:"19px"}}></i></Button>
                        <Button size="sm"><i className="fas fa-save" style={{fontSize:"19px"}}></   i></Button>    
                        </div>
                        <textarea style={{width:"100%",border:"none"}} ref={this.InputRef} autoCapitalize="false" tabIndex="4">
                        </textarea>
                    </div>
                </Col>
            </Row>
        );
    }
}
export default Editor;
