import React from "react";
import NavBar from "./NavBar";
import {Row} from 'reactstrap';
import Leftcomponent from "./Leftcomponent";
import Footer from "./Footer";
import Rightcomponent from "./Rightcomponent";
import base, {firebaseApp} from "./base";
import firebase ,{auth} from "firebase";
import Authanticate from "./Authanticate";

class App extends React.Component{
    state={
        editor:{},
        isopen:false,
        savefiles:{},
        mode:"none",
        dropdownvalue:"",
        issaved:false,
        filename:"",
        status:"",
        id:null,
        recent:{},
        val:{},
        length:-9999999999,
        uid:null,
        owner:null,
        signup:false,
        fontsize:""
        
    }
    componentWillMount(){
        firebase.auth().onAuthStateChanged(user=>{
            if(user){  
                this.authHandler({user});
            }
        })
      
    }
    componentDidMount(){
        const res=localStorage.getItem("CodeBix");
        if(res){
            const val=JSON.parse(res);
            console.log(val);
            this.setState({val:val});
            this.setState({recent:val.recent});
        }

        const id = this.props.match.params.id;
        if(id){
            setTimeout(() => {
                this.updateeditor(id);
            },3400);
        }
    }

     componentWillUnmount(){
         if(this.state.length>0 && this.state.length < this.state.editor.getValue().length){
             this.savedlastfile();
         }
        base.removeBinding(this.ref);
    }
   
    componentDidUpdate(){
        const final={
            mode:this.state.dropdownvalue || null,
            fontsize:this.state.fontsize || null,
            recent:this.state.recent
        }
        localStorage.setItem("CodeBix",JSON.stringify(final));

    }

    previoussetting=()=>{
        const res=localStorage.getItem("CodeBix");
        var val = JSON.parse(res);
        if(val && val.mode && val.fontsize){
            this.state.editor.getWrapperElement().style["font-size"]= val+"px";
            this.state.editor.refresh();
            switch (val.mode) {
                case "C++":
                    this.props.setmode("C++");
                    this.props.editor.setOption("mode","text/x-c++src");
                    this.props.editor.setValue(cpp);
                    this.props.setlength(this.props.editor.getValue().length);
                    break;
                case "C":
                    this.setmode("C++");
                    this.state.editor.setOption("mode","text/x-c++src");
                    this.state.editor.setValue(cpp);
                    this.props.setlength(this.props.editor.getValue().length);
                default:
                    break;
            }
        }        
    }

    setsignup=()=>{
        this.setState({signup:!this.state.signup});
    }

    savedlastfile=()=>{
        const len =Object.keys(this.state.recent).length;
        if(len<5){
            let id =null;
            let data={};
            if(!this.state.issaved){                
               const file={
                   name:"Unknown",
                   content:this.state.editor.getValue(),
                   mode:this.state.dropdownvalue,
                   status:"Unsaved"
               }
               id=Date.now();
               data=file;
            }
            else{
                id=this.state.id;
               data=this.state.savefiles[this.state.id];
            }
            let recent={...this.state.recent};
            recent[id]=data;
            const final={
                mode:"",
                fontsize:"",
                recent:recent
            }
            localStorage.setItem("CodeBix",JSON.stringify(final));
            this.setState({recent});
            
        }
        else{
           const index= Object.keys(this.state.recent)[0];
           let recent={...this.state.recent};
           delete recent[index];
           let id =null;
            let data={};
            if(!this.state.issaved){
               const file={
                   name:"Unknown",
                   content:this.state.editor.getValue(),
                   mode:this.state.dropdownvalue,
                   status:"Unsaved"
               }
               id=Date.now();
               data=file;
            }
            else{
                id=this.state.id;
               data=this.state.savefiles[this.state.id];
            }
            recent[id]=data;
            const final={
                mode:"",
                fontsize:"",
                recent:recent
            }
            localStorage.setItem("CodeBix",JSON.stringify(final));
            this.setState({recent:recent});
            
        }
    }

    updaterecent=()=>{
        const len =Object.keys(this.state.recent).length;
        if(len<5){
            let id =null;
            let data={};
            if(!this.state.issaved){                
               const file={
                   name:"Unknown",
                   content:this.state.editor.getValue(),
                   mode:this.state.dropdownvalue,
                   status:"Unsaved"
               }
               id=Date.now();
               data=file;
            }
            else{
                id=this.state.id;
               data=this.state.savefiles[this.state.id];
            }
            let recent={...this.state.recent};
            recent[id]=data;
            this.setState({recent});
            
        }
        else{
           const index= Object.keys(this.state.recent)[0];
           let recent={...this.state.recent};
           delete recent[index];
           let id =null;
            let data={};
            if(!this.state.issaved){
               const file={
                   name:"Unknown",
                   content:this.state.editor.getValue(),
                   mode:this.state.dropdownvalue,
                   status:"Unsaved"
               }
               id=Date.now();
               data=file;
            }
            else{
                id=this.state.id;
               data=this.state.savefiles[this.state.id];
            }
            recent[id]=data;
            this.setState({recent:recent});
        }
      
    }

    setid=(val)=>{
        this.setState({id:val});
    }
    setdropdown=(val)=>{
        this.setState({dropdownvalue:val});
    }
    setsaved=()=>{
        this.setState({issaved:!this.state.issaved});
    }

    updateeditor=(id)=>{
        if(id && this.state.savefiles[id]){
            const data = this.state.savefiles[id];
            this.setid(id);
            this.setsaved();
            this.setdropdown(data.mode);
            this.state.editor.setValue(data.content);
            switch (data.mode) {
                case "C++":
                    this.state.editor.setOption("mode","text/x-c++src");
                    break;
                case "C":
                    this.state.editor.setOption("mode","text/x-csrc");
                    break;
            }
            this.setState({length:this.state.editor.getValue().length});   
            this.state.editor.on('change',(cm)=>{
                if(this.state.dropdownvalue===data.mode){
                    data.content=cm.getValue();
                    this.updatevalue(id,data);
                
                }
            });
        
        }
    }

    updatevalue=(key,val)=>{
        const savefiles={...this.state.savefiles};
        savefiles[key]=val;
        this.setState({savefiles:savefiles});        
    }

    setmode=(val)=>{
        this.setState({mode:val});
    }
    savefile=(val)=>{
        this.setState({issaved:true});  
        this.setstatus("Saving...");
        const data={
            name:val,
            content:this.state.editor.getValue(),
            status:"saved",
            mode:this.state.mode
        }
        const savefiles ={...this.state.savefiles};
        const id =Date.now();
        this.setid(id);
        savefiles[id]=data;
        this.setState({savefiles:savefiles});
        setTimeout(() => {
            this.setstatus(data.name);
            this.updaterecent();
        }, 1000);
        
        this.state.editor.on('change',(cm)=>{
            if(this.state.dropdownvalue===data.mode){
                this.setstatus("Saving...");
                data.content=cm.getValue();
                this.updatevalue(id,data);
                setTimeout(() => {
                    this.setstatus(data.name);
                }, 1000);

            }
        });
    }
    seteditor=(val)=>{
        this.setState({editor:val});
    }
    settheme=(val)=>{
        this.state.editor.setOption("theme",val);
    }
    settab=(val)=>{
        this.state.editor.setOption("indentUnit",val);
    }
    setsize=(val)=>{
        this.setState({fontsize:val});
        this.state.editor.getWrapperElement().style["font-size"]= val+"px";
        this.state.editor.refresh();
    }
    toggle=()=>{
        this.setState({isopen:!this.state.isopen});   
    }
    setstatus=(val)=>{
        // console.log(val);
        this.setState({status:val});
    }

    setlength=(len)=>{
        this.setState({length:len});
    }

    authenticate=(provider)=>{
        const authprovider =new firebase.auth[`${provider}AuthProvider`]();
        firebase.auth().signInWithPopup(authprovider).then(this.authHandler); 
        this.setsignup();
       
    }

    authHandler= async (authdata)=>{
        // look up a store ito the firebase.
        const store = await base.fetch(authdata.user.uid,{context:this});
        //claim if there is no owner.
        if(!store.user){
            await base.post(`${authdata.user.uid}/user`,{
                data:authdata.user.uid
            });
        }
        //set the state of the inventory compnent.
        this.setState({
            uid:authdata.user.uid,
            owner:store.user || authdata.user.uid
        });
        this.ref= base.syncState(`${authdata.user.uid}/savefiles`,{
            context:this,
            state:'savefiles'
        });

    }
    logout = async () =>{
        await firebase.auth().signOut();
        this.setState({
            uid:null
        });
    }

    editrecent=(val,ind)=>{
        this.setState({issaved:false});
        if(val.status==="saved"){
            this.setsaved();
            this.setstatus(val.name);
        }
        
        this.setid(ind);
        this.setdropdown(val.mode);
        switch (val.mode) {
            case "C++":
                this.state.editor.setOption("mode","text/x-c++src");
                break;
            case "C":
                this.state.editor.setOption("mode","text/x-csrc");
                break;
        }
        this.setState({filename:val.name});
        this.state.editor.setValue(val.content);
        this.state.editor.on('change',(cm)=>{
            if(this.state.dropdownvalue===val.mode && val.status==="saved"){
                val.content=cm.getValue();
                this.updatevalue(val,ind);
            }
        });
    }

    render(){
        return(
            <div className="container-fluid" style={{paddingLeft:"0px",paddingRight:"0px"}}>
