import React from "react";
import {Row, Col, Button } from 'reactstrap';
class CustomInputs extends React.Component{
    box=React.createRef();
    state={
        isopen:false
    }
    handleclick=(event)=>{
        console.log(this.box);
        // if(!this.state.isopen){
        //     this.box.style.height="0px";
        // }
        // else{
        //     this.box.style.height="100%";
        // }
    }   

    render(){
        return(
            <div style={{width:"100%",height:"100%"}}>
                <Row>
                    <Col lg="6" md="6" sm="12" xs="12">
                        <Button color="primary" size="md" block onClick={this.handleclick}>Inputs <i class="fas fa-angle-down"></i></Button>
                    </Col>
                    <Col lg="6" md="6" sm="12" xs="12">
                        <Button color="primary" size="md" block><i class="fas fa-play"></i> Run</Button>
                    </Col>
                </Row>
                <Row ref={this.box}>
                    <Col lg="6" md="12" sm="12" xs="12">
                        <div className="box">
                            <div className="heading"><h5>Input</h5></div>
                            <textarea style={{width:"100%",height:"150px"}}>Standard input here...</textarea>
                        </div>
                    </Col>
                    <Col lg="6" md="12" sm="12" xs="12">
                        <div className="box">
                            <div className="heading"><h5>Output</h5></div>
                            <textarea style={{width:"100%",height:"150px"}}>Standard out here...</textarea>
                        </div>
                    </Col>
                </Row> 
            </div>
        );
    }
}
export default CustomInputs;
