import React from "react";
import { Row,Col,Button } from "reactstrap";
import { UncontrolledButtonDropdown, DropdownMenu, DropdownItem, DropdownToggle } from 'reactstrap';


class Editor extends React.Component{

    render(){
        return(
            <Row>
                <Col lg="12" md="12" sm="12" xs="12">
                    <div className="editor">
                        <div className="editor-nav">
                        <div class="btn-group dropright">
  <button type="button" class="btn btn-secondary dropdown-toggle" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
    Dropright
  </button>
  <div class="dropdown-menu">
    {/* <!-- Dropdown menu links --> */}
    <button class="dropdown-item" type="button">Action</button>
    <button class="dropdown-item" type="button">Another action</button>
    <button class="dropdown-item" type="button">Something else here</button>
  </div>
</div>



                            <Button  size="sm"><i class="fas fa-compress" style={{fontSize:"19px"}}></i></Button>
                            <Button  size="sm"><i class="fas fa-share-alt" style={{fontSize:"19px"}}></i></Button>
                            <Button size="sm"><i class="fas fa-save" style={{fontSize:"19px"}}></i></Button>
                        </div>
                        {/* <div className="vertical-num">
                            
                        </div> */}
                        <textarea style={{height:"400px",width:"100%",border:"none"}} className="textbox">
                        
                        </textarea>
                    </div>
                </Col>
            </Row>
        );
    }
}
export default Editor;
