import React from "react";
import {Col} from 'reactstrap';
import Editor from "./Editor";
import CustomInputs from "./CustomInputs";
import Inputbox from "./Inputbox";
import Output from "./Output";
import Share from "./Share";
class Leftcomponent extends React.Component{
    state={
        model:false,
        opmodel:false,
        inputdata:"",
        isShare:false
    }

    setshare=()=>{
        this.setState({isShare:!this.state.isShare});
    }
    toggle=()=>{
        if(!this.state.model){
            this.setState({model:true});
        }
        else{
            this.setState({model:false});
        }
    }
    toggleoutput=()=>{
        if(!this.state.opmodel){
            this.setState({opmodel:true});
        }
        else{
            this.setState({opmodel:false});
        }
    }

    updateinputdata=(val)=>{
        this.setState({inputdata:val});
    }

    seteditor=(val)=>{
        this.props.seteditor(val);
    }
    
    render(){
        return(
            <>
                <Share isShare={this.state.isShare} setshare={this.setshare} path={this.props.path} id={this.props.id}/>
                <Col lg="9" md="12" sm="12" xs="12">
                    <Editor setoldvalue={this.props.setoldvalue} oldvalue={this.props.oldvalue} setlength={this.props.setlength} length={this.props.length} editor={this.props.editor} seteditor={this.seteditor} toggle={this.props.toggle} setmode={this.props.setmode} setdropdown={this.props.setdropdown} dropdownvalue={this.props.dropdownvalue} issaved={this.props.issaved} setsaved={this.props.setsaved} status={this.props.status} setstatus={this.props.setstatus} setshare={this.setshare} updaterecent={this.props.updaterecent}/>
                    <CustomInputs toggle={this.toggle}/>
                    <Inputbox model={this.state.model} toggle={this.toggle} inputdata={this.state.inputdata} updateinputdata={this.updateinputdata}/>
                    <Output model={this.state.opmodel} toggle={this.toggleoutput}/>
                </Col>
            </>
        );
    }
}
export default Leftcomponent;
