import React from "react";
import {Col, Input } from 'reactstrap';
import Editor from "./Editor";
import CustomInputs from "./CustomInputs";
import Inputbox from "./Inputbox";
import Output from "./Output";
class Leftcomponent extends React.Component{
    state={
        model:false,
        opmodel:false,
        inputdata:"",
    }

    toggle=()=>{
        if(!this.state.model){
            this.setState({model:true});
        }
        else{
            this.setState({model:false});
        }
    }
    toggleoutput=()=>{
        if(!this.state.opmodel){
            this.setState({opmodel:true});
        }
        else{
            this.setState({opmodel:false});
        }
    }

    updateinputdata=(val)=>{
        this.setState({inputdata:val});
    }

    seteditor=(val)=>{
        this.props.seteditor(val);
    }
    
    render(){
        return(
            <Col lg="9" md="9" sm="12" xs="12">
                <Editor editor={this.state.editor} seteditor={this.seteditor}/>
                <CustomInputs toggle={this.toggle}/>
                <Inputbox model={this.state.model} toggle={this.toggle} inputdata={this.state.inputdata} updateinputdata={this.updateinputdata}/>
                <Output model={this.state.opmodel} toggle={this.toggleoutput}/>
            </Col>
        );
    }
}
export default Leftcomponent;
