import React from "react";
import NavBar from "./NavBar";
import { Row, Col} from "reactstrap";
import Base from "./base";
import Savecards from "./Savecards";
import Filecontainer from "./Filecontainer";

class Files extends React.Component{
    state={
        savefiles:[],
        result:[]
    }
    componentDidMount(){
        this.ref= Base.syncState("/tulsisharma/savefiles",{
            context:this,
            state:"savefiles"
        });
    }
    componentWillUnmount(){
        Base.removeBinding(this.ref);
    }

    handlechange=(event)=>{
        const val = event.currentTarget.value;
        const result=[];
        this.state.savefiles.map(key=>{
            if(this.state.savefiles[key].mode===val){
                result[key]=this.state.savefiles[key];
            }
        });
        this.setState({result:result});
    }

    render(){
        return(
            <div className="container-fluid" style={{padding:"0px"}}>
                <div className="wrapper-box">
                    <NavBar/>
                    <div className="container" style={{}}>
                        <br></br>
                        <div className="back-div">
                            <Row>
                                <Col lg="5" md="6" sm="9" xs="9" >
                                    <select onChange={this.handlechange}  style={{display:"block",border:"block",width:"100%",height:"35px",borderColor:"black",borderWidth:"2px"}}>
                                        <option selected value="C++">C++</option>
                                        <option value="C">C</option>
                                    </select>
                                    
                                </Col>
                            </Row>
                        </div>
                        <br></br>
                        <Filecontainer/>
                    </div>
                </div>
            </div>
        );

    }
}
export default Files;
