import React from "react";
import { Row, Col,Container, Button} from "reactstrap";
import Filecontainer from "./Filecontainer";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import Share from "./Share";
import base, {firebaseApp} from "./base";
import firebase ,{auth} from "firebase";
class Files extends React.Component{
    preRef=React.createRef();
    nextRef=React.createRef();
    firstRef=React.createRef();
    lastRef=React.createRef();
    state={
        savefiles:{},
        result:null,
        flag:false,
        isShare:false,
        id:null,
        uid:null,
        owner:null,
        pagelist:null,
        currentPage : 1,
        numberPerPage :9,
        numberOfPages : 1
    };
    componentWillMount(){
        firebase.auth().onAuthStateChanged(user=>{
            if(user){  
                this.authHandler({user});
            }
        })
      
    }
    componentDidMount(){
      this.ref= base.syncState("//savefiles",{
        context:this,
        state: 'savefiles'
      }
      );
    }
    componentWillUnmount(){
        base.removeBinding(this.ref);
    }
    authHandler= async (authdata)=>{
        // look up a store ito the firebase.
        const store = await base.fetch(authdata.user.uid,{context:this});
        //claim if there is no owner.
        if(!store.user){
            await base.post(`${authdata.user.uid}/user`,{
                data:authdata.user.uid
            });
        }
        //set the state of the inventory compnent.
        this.setState({
            uid:authdata.user.uid,
            owner:store.user || authdata.user.uid
        });
            this.ref= base.syncState(`${authdata.user.uid}/savefiles`,{
                context:this,
                state:'savefiles'
            });
   

    }

    setshare=()=>{
        this.setState({isShare:!this.state.isShare});
    }

    handlechange=(event)=>{
        if(!this.state.flag){
            this.setState({flag:true});
        }
        this.setState({result:null});
        const result={};
        const val = event.currentTarget.value;
        if(val==="Choose language"){
            this.setState({flag:false});
        }
        setTimeout(() => {
            Object.keys(this.state.savefiles).map((key)=>{
                if(this.state.savefiles[key].mode===val){
                    result[key]=this.state.savefiles[key];
                }
            });
            console.log(this.state.result); 
            console.log(result);    
            this.setState({result:result});
        },3300);
        
    }
    handledelete=(ind)=>{
        let savefiles = {...this.state.savefiles};
        let result = {...this.state.result};
        console.table(savefiles);
        console.log("######################################");
        savefiles[ind]=null;
        console.table(savefiles);
        this.setState({savefiles:savefiles});
        result[ind]=null;
        this.setState({result});
        console.log("######################################");

        console.table(this.state.savefiles);  
    }
    editfile=(val)=>{
        const win=window.open(`/files/${val}`,'_blank');
        if(win){
            win.focus();
        }
        base.removeBinding(this.ref);

    }
    setid=(val)=>{
        this.setState({id:val});
    }

    getNumberOfPages=()=>{
        this.setState({numberOfPages:Math.ceil(this.state.recent.length / this.state.numberPerPage)})
    }
    nextPage=()=>{
        this.loadList(this.state.currentPage+1);
        this.setState({currentPage:this.state.currentPage+1});
    }
    previousPage=()=> {
        this.loadList(this.state.currentPage-1);
        this.setState({currentPage:this.state.currentPage-1});
    }
    firstPage() {
        this.loadList(1);
        this.setState({currentPage:1});
    }
    lastPage=()=> {
        this.loadList(this.state.numberOfPages);
        this.setState({currentPage:this.state.numberOfPages});
    }
    loadList=()=> {
        var begin = ((this.state.currentPage - 1) * this.state.numberPerPage);
        var end = begin + this.state.numberPerPage;
    
        this.setState({pagelist:this.state.result.slice(begin, end)});
        this.check();         // determines the states of the pagination buttons
    }
    check=()=>{

    }
    render(){
        return(
            <div className="container-fluid" style={{padding:"0px"}}>
                <div className="wrapper-box">
                    <nav style={{textAlign:"center",padding:"14px",background:"whitesmoke"}}>
                        <div className="container">
                            <h5>
                                <Link style={{textDecoration:"none"}} to="/">
                                    <i className="fas fa-code"  style={{color:"black"}} ></i>
                                    <span style={{color:"gray",paddingLeft:"3px",fontWeight:"200"}}>CodeBix</span>
                                </Link>

                            </h5>
                            
                        </div>
                        
                    </nav>
                    <Share isShare={this.state.isShare} setshare={this.setshare} path={this.props.match.url} id={this.state.id} />
                    <div className="container">
                        <br></br>
                        <div className="back-div">
                            <Row>
                                <Col lg="5" md="6" sm="9" xs="9" >
                                    <select onChange={this.handlechange}  style={{display:"block",border:"block",width:"100%",height:"35px",borderColor:"black",borderWidth:"2px"}}>
                                        <option selected value="Choose language">Choose language</option>
                                        
                                        <option value="C++">C++</option>
                                        <option value="C">C</option>
                                    </select>
                                    
                                </Col>
                            </Row>
                        </div>
                        <br></br>
                        <Container style={{border:"1px solid lightgray",paddingBottom:"20px",marginTop:"10px",minHeight:"450px"}}>
                            <Filecontainer result={this.state.result} flag={this.state.flag} handledelete={this.handledelete} editfile={this.editfile} setid={this.setid} setshare={this.setshare}/>
                        </Container>
                        <Row>
                            <Col lg="4" md="2" sm="2" xs="2"></Col>
                            <Col lg="4" md="6" sm="10" xs="10">
                                <div className="pagination">
                                    <Button id="previous" ref={this.preRef} onClick={()=>this.previousPage()}>Prev</Button>
                                    <Button id="first" ref={this.firstRef} onClick={()=>this.firstPage()}>First</Button>
                                    <Button id="next" ref={this.nextRef} onClick={()=>this.nextPage()}>Next</Button>
                                    <Button id="last" ref={this.lastRef} onClick={()=>this.lastPage()}>Last</Button>
                                </div>
                            </Col>
                            <Col lg="4" md="2" sm="2"></Col>
                            
                        </Row>
                    </div>
                </div>
            </div>
        );

    }
}
export default Files;
