import React from "react";
import {Row, Col, Button } from 'reactstrap';
class CustomInputs extends React.Component{
    inputRef=React.createRef();
    state={
        isopen:false
    }
    handleclick=(event)=>{
        console.log(this.inputRef.current);
        if(!this.state.isopen){
            this.setState({isopen:true})
            console.log("open");
            this.inputRef.current.style.display="block";
        }
        else{
            this.setState({isopen:false})
            console.log("close");
            this.inputRef.current.style.display="none";
        }
    }   

    render(){
        return(
            <div style={{width:"100%",height:"100%"}}>
                <Row>
                    <Col lg="6" md="6" sm="12" xs="12">
                        <Button color="primary" size="md" block onClick={this.handleclick}>Inputs <i className="fas fa-angle-down"></i></Button>
                    </Col>
                    <Col lg="6" md="6" sm="12" xs="12">
                        <Button color="primary" size="md" block><i className="fas fa-play"></i> Run</Button>
                    </Col>
                </Row>
                <Row className="hidden-box">
                    <Col  lg="12" md="12" sm="12" xs="12">
                        <div ref={this.inputRef}>
                            <Row>                           
                                <Col lg="6" md="12" sm="12" xs="12">
                                    <div className="box">
                                        <div className="heading"><h5>Input</h5></div>
                                        <textarea defaultValue="Standard input here..."></textarea>
                                    </div>
                                </Col>
                                <Col lg="6" md="12" sm="12" xs="12">
                                    <div className="box">
                                        <div className="heading"><h5>Output</h5></div>
                                        <textarea defaultValue="Standard out here..."></textarea>
                                    </div>
                                </Col>
                            </Row>
                        </div>
                    </Col>
                </Row> 
            </div>
        );
    }
}
export default CustomInputs;
