import React from "react";
import NavBar from "./NavBar";
import {Row} from 'reactstrap';
import Leftcomponent from "./Leftcomponent";
import Footer from "./Footer";
import Rightcomponent from "./Rightcomponent";
import base from "./base";
import {firebaseApp} from "./base";
class App extends React.Component{
    constructor(){
        super();
        
    }
    state={
        editor:{},
        isopen:false,
        savefiles:{},
        mode:"none",
        dropdownvalue:"",
        issaved:false,
        filename:"",
        status:"",
        id:null,
        recent:{}

        
    }
    
    componentDidMount(){
        this.ref= base.syncState("/tulsisharma/savefiles",{
            context:this,
            state:'savefiles'
        });
        const res=localStorage.getItem("CodeBix");
        if(res){
            const val=JSON.parse(res);
            console.log(val);
            this.setState({recent:val.recent});
        }

        const id = this.props.match.params.id;
        if(id){
            setTimeout(() => {
                this.updateeditor(id);
            },3400);
        }
    }

    componentWillUnmount(){
        this.updaterecent();
        base.removeBinding(this.ref);
    }
    componentDidUpdate(){
        const final={
            mode:"",
            fontsize:"",
            recent:this.state.recent
        }
        localStorage.setItem("CodeBix",JSON.stringify(final));

    }

    updaterecent=()=>{
        const len =Object.keys(this.state.recent).length;
        if(len<5){
            let id =null;
            let data={};
            if(!this.state.issaved){
                console.log("4");
                
               const file={
                   name:"Unknown",
                   content:this.state.editor.getValue(),
                   mode:this.state.dropdownvalue,
                   status:"Unsaved"
               }
               id=Date.now();
               data=file;
            }
            else{
                id=this.state.id;
               data=this.state.savefiles[this.state.id];
            }
            let recent={...this.state.recent};
            recent[id]=data;
            this.setState({recent});
            
        }
        else{
           const index= Object.keys(this.state.recent)[0];
           let recent={...this.state.recent};
           delete recent[index];
           let id =null;
            let data={};
            if(!this.state.issaved){
               const file={
                   name:"Unknown",
                   content:this.state.editor.getValue(),
                   mode:this.state.dropdownvalue,
                   status:"Unsaved"
               }
               id=Date.now();
               data=file;
            }
            else{
                id=this.state.id;
               data=this.state.savefiles[this.state.id];
            }
            recent[id]=data;
            this.setState({recent});
        }
      
    }

    setid=(val)=>{
        this.setState({id:val});
    }
    setdropdown=(val)=>{
        this.setState({dropdownvalue:val});
    }
    setsaved=()=>{
        this.setState({issaved:!this.state.issaved});
    }

    updateeditor=(id)=>{
        if(id && this.state.savefiles[id]){
            const data = this.state.savefiles[id];
            this.setid(id);
            this.setsaved();
            this.setdropdown(data.mode);
            this.state.editor.setValue(data.content);
            switch (data.mode) {
                case "C++":
                    this.state.editor.setOption("mode","text/x-c++src");
                    break;
                case "C":
                    this.state.editor.setOption("mode","text/x-csrc");
                    break;
            }
            this.updaterecent();
                
            this.state.editor.on('change',(cm)=>{
                if(this.state.dropdownvalue===data.mode){
                    data.content=cm.getValue();
                    this.updatevalue(id,data);
                
                }
            });
        
        }
    }

    updatevalue=(key,val)=>{
        const savefiles={...this.state.savefiles};
        savefiles[key]=val;
        this.setState({savefiles:savefiles});        
    }

    setmode=(val)=>{
        this.setState({mode:val});
    }
    savefile=(val)=>{
        this.setState({issaved:true});  
        this.setstatus("Saving...");
        const data={
            name:val,
            content:this.state.editor.getValue(),
            status:"saved",
            mode:this.state.mode
        }
        const savefiles ={...this.state.savefiles};
        const id =Date.now();
        this.setid(id);
        savefiles[id]=data;
        this.setState({savefiles:savefiles});
        setTimeout(() => {
            this.setstatus(data.name);
            this.updaterecent();
        }, 1000);
        
        this.state.editor.on('change',(cm)=>{
            if(this.state.dropdownvalue===data.mode){
                this.setstatus("Saving...");
                data.content=cm.getValue();
                this.updatevalue(id,data);
                setTimeout(() => {
                    this.setstatus(data.name);
                }, 1000);

            }
        });
    }
    seteditor=(val)=>{
        this.setState({editor:val});
    }
    settheme=(val)=>{
        this.state.editor.setOption("theme",val);
    }
    settab=(val)=>{
        this.state.editor.setOption("indentUnit",val);
    }
    setsize=(val)=>{
        this.state.editor.getWrapperElement().style["font-size"]= val+"px";
        this.state.editor.refresh();
    }
    toggle=()=>{
        this.setState({isopen:!this.state.isopen});   
    }
    setstatus=(val)=>{
        // console.log(val);
        this.setState({status:val});
    }
    render(){
        return(
            <div className="container-fluid" style={{paddingLeft:"0px",paddingRight:"0px"}}>
                <div className="main-wrapper">
                    <NavBar settheme={this.settheme} settab={this.settab} setsize={this.setsize} isopen={this.state.isopen} savefile={this.savefile} toggle={this.toggle}/>
                    <div className="gap"></div>
                    <div className="" style={{marginLeft:"20px",marginRight:"20px"}}>
                        <Row>
                            <Leftcomponent setsaved={this.setsaved} editor={this.state.editor} seteditor={this.seteditor} toggle={this.toggle} setmode={this.setmode} setdropdown={this.setdropdown} dropdownvalue={this.state.dropdownvalue} issaved={this.state.issaved} status={this.state.status} setstatus={this.setstatus} path={this.props.match.url} id={this.state.id} updaterecent={this.updaterecent}/>
                            <Rightcomponent recent={this.state.recent}/>
                        </Row>
                    </div>
                    <br></br>
                    <Footer/>
                </div>
            </div>
        );
    }
}

export default App;
