import React from "react";
import { Row,Col,Button,Dropdown } from "reactstrap";



class Editor extends React.Component{

    render(){
        return(
            <Row>
                <Col lg="12" md="12" sm="12" xs="12">
                    <div className="editor">
                        <div className="editor-nav">
                        <Dropdown isOpen={isOpen} toggle={toggle}>
  <DropdownToggle>
    Dropdown
  </DropdownToggle>
  <DropdownMenu
    modifiers={{
      setMaxHeight: {
        enabled: true,
        order: 890,
        fn: (data) => {
          return {
            ...data,
            styles: {
              ...data.styles,
              overflow: 'auto',
              maxHeight: '100px',
            },
          };
        },
      },
    }}
  >
    <DropdownItem>Another Action</DropdownItem>
    <DropdownItem>Another Action</DropdownItem>
    <DropdownItem>Another Action</DropdownItem>
    <DropdownItem>Another Action</DropdownItem>
    <DropdownItem>Another Action</DropdownItem>
    <DropdownItem>Another Action</DropdownItem>
    <DropdownItem>Another Action</DropdownItem>
    <DropdownItem>Another Action</DropdownItem>
    <DropdownItem>Another Action</DropdownItem>
    <DropdownItem>Another Action</DropdownItem>
  </DropdownMenu>
</Dropdown>



                            <Button  size="sm"><i class="fas fa-compress" style={{fontSize:"19px"}}></i></Button>
                            <Button  size="sm"><i class="fas fa-share-alt" style={{fontSize:"19px"}}></i></Button>
                            <Button size="sm"><i class="fas fa-save" style={{fontSize:"19px"}}></i></Button>
                        </div>
                        {/* <div className="vertical-num">
                            
                        </div> */}
                        <textarea style={{height:"400px",width:"100%",border:"none"}} className="textbox">
                        
                        </textarea>
                    </div>
                </Col>
            </Row>
        );
    }
}
export default Editor;
