import React from "react";
import NavBar from "./NavBar";
import { Container, Row, Input } from 'reactstrap';
import Leftcomponent from "./Leftcomponent";
import Footer from "./Footer";
import Rightcomponent from "./Rightcomponent";
class App extends React.Component{
    state={
        editor:{}
    }
    seteditor=(val)=>{
        this.setState({editor:val});
    }
    settheme=(val)=>{
        this.state.editor.setOption("theme",val);
    }
    settab=(val)=>{
        console.log("hlooe");
        this.state.editor.setOption("indentUnit",val);
    }
    setsize=(val)=>{
        this.state.editor.getWrapperElement().style["font-size"]= val+"px";
        this.state.editor.refresh();
    }
    render(){
        return(
            <div className="app">
                <div className="main-wrapper">
                    <NavBar settheme={this.settheme} settab={this.settab} setsize={this.setsize}/>
                    <div className="gap"></div>
                    <div className="container-fuild">
                        <Row>
                            <Leftcomponent editor={this.state.editor} seteditor={this.seteditor}/>
                            <Rightcomponent/>
                        </Row>
                    </div>
                    <br></br>
                    <Footer/>
                </div>
            </div>
        );
    }
}

export default App;
