import React from "react";
import NavBar from "./NavBar";
import { Row, Col,Container} from "reactstrap";
import base from "./base";
import Filecontainer from "./Filecontainer";

class Files extends React.Component{
    state={
        savefiles:{},
        result:null,
        flag:false
    };
    componentDidMount(){
      this.ref= base.syncState("/tulsisharma/savefiles",{
        context:this,
        state: 'savefiles'
      }
      );
    }
    componentWillUnmount(){
        base.removeBinding(this.ref);
    }

    handlechange=(event)=>{
        if(!this.state.flag){
            this.setState({flag:true});
        }
        this.setState({result:null});
        const result={};
        const val = event.currentTarget.value;
        if(val==="Choose language"){
            this.setState({flag:false});
        }
        setTimeout(() => {
            Object.keys(this.state.savefiles).map((key)=>{
                if(this.state.savefiles[key].mode===val){
                    result[key]=this.state.savefiles[key];
                }
            });
            console.log(this.state.result); 
            console.log(result);    
            this.setState({result:result});
        },3200);
        
    }
    handledelete=(ind)=>{
        let savefiles = {...this.state.savefiles};
        let result = {...this.state.result};
        console.table(savefiles);
        console.log("######################################");
        savefiles[ind]=null;
        console.table(savefiles);
        this.setState({savefiles:savefiles});
        result[ind]=null;
        this.setState({result});
        console.log("######################################");

        console.table(this.state.savefiles);  
    }
    editfile=(val)=>{
        const win=window.open(`/files/${val}`,'_blank');
        if(win){
            win.focus();
        }
        base.removeBinding(this.ref);

    }
    
    render(){
        return(
            <div className="container-fluid" style={{padding:"0px"}}>
                <div className="wrapper-box">
                    <NavBar/>
                    <div className="container" style={{}}>
                        <br></br>
                        <div className="back-div">
                            <Row>
                                <Col lg="5" md="6" sm="9" xs="9" >
                                    <select onChange={this.handlechange}  style={{display:"block",border:"block",width:"100%",height:"35px",borderColor:"black",borderWidth:"2px"}}>
                                        <option selected value="Choose language">Choose language</option>
                                        
                                        <option value="C++">C++</option>
                                        <option value="C">C</option>
                                    </select>
                                    
                                </Col>
                            </Row>
                        </div>
                        <br></br>
                        <Container style={{border:"1px solid lightgrey",paddingBottom:"20px",marginTop:"10px",minHeight:"400px"}}>
                            <Filecontainer result={this.state.result} flag={this.state.flag} handledelete={this.handledelete} editfile={this.editfile}/>
                        </Container>
                    </div>
                </div>
            </div>
        );

    }
}
export default Files;
