import React from "react";
import NavBar from "./NavBar";
import {Row} from 'reactstrap';
import Leftcomponent from "./Leftcomponent";
import Footer from "./Footer";
import Rightcomponent from "./Rightcomponent";
import base from "./base";
import {firebaseApp} from "./base";
class App extends React.Component{
    constructor(){
        super();
        
    }
    state={
        editor:{},
        isopen:false,
        savefiles:{},
        mode:"none",
        dropdownvalue:"",
        issaved:false,
        filename:"",
        status:"",
        id:null,
        recent:{
            1234445433:{
            name:"HellowWorld",
            content:"none",
            status:"saved",
            mode:"cpp"
            }
        },
        front:0,
        rear:-1

        
    }
    
    componentDidMount(){
        this.ref= base.syncState("/tulsisharma/savefiles",{
            context:this,
            state:'savefiles'
        });
        const res=JSON.parse(localStorage.getItem("CodeBix"));
        if(res){
            this.setState({recent:res.recent});
        }

        const id = this.props.match.params.id;
        if(id){
            setTimeout(() => {
                this.updateeditor(id);
            },3400);
        }
    }

    componentWillUnmount(){
        this.updaterecent();
        base.removeBinding(this.ref);
    }

    componentDidUpdate(){
        localStorage.setItem("CodeBix",JSON.stringify(this.state.recent));
    }

    updaterecent=()=>{
        const len = Object.keys(this.state.recent).length;
        // let content=this.state.editor.getValue();
        // let dropval=this.state.dropdownvalue;
        if(len<5){
