            //        mode:dropval,
            //        status:"Unsaved"
            //    }
            //    id=Date.now();
            //    data=file;
            // }
            // else{
            //     id=this.state.id;
            //    data=this.state.savefiles[this.state.id];
            // }
            // const recent={...this.state.recent};
            // recent[id]=data;
            // this.setState({recent});
            
        }
        else{
        //    const index= Object.keys(this.state.recent)[0];
        //    const recent={...this.state.recent};
        //    delete recent[index];
        //    const id =null;
        //     const data={};
        //     if(!this.state.issaved){
        //        const file={
        //            name:"Unknown",
        //            content:this.state.editor.getValue(),
        //            mode:this.state.dropdownvalue,
        //            status:"Unsaved"
        //        }
        //        id=Date.now();
        //        data=file;
        //     }
        //     else{
        //         id=this.state.id;
        //        data=this.state.savefiles[this.state.id];
        //     }
        //     recent[id]=data;
        //     this.setState({recent});
        }
    }

    setid=(val)=>{
        this.setState({id:val});
    }
    setdropdown=(val)=>{
        this.setState({dropdownvalue:val});
    }
    setsaved=()=>{
        this.setState({issaved:!this.state.issaved});
    }

    updateeditor=(id)=>{
        if(id && this.state.savefiles[id]){
            const data = this.state.savefiles[id];
            this.setid(id);
            this.setsaved();
            this.setdropdown(data.mode);
            this.state.editor.setValue(data.content);
            switch (data.mode) {
                case "C++":
                    this.state.editor.setOption("mode","text/x-c++src");
                    break;
                case "C":
                    this.state.editor.setOption("mode","text/x-csrc");
                    break;
            }
            this.updaterecent();
                
            this.state.editor.on('change',(cm)=>{
                if(this.state.dropdownvalue===data.mode){
                    data.content=cm.getValue();
                    this.updatevalue(id,data);
                
                }
            });
        
        }
    }

    updatevalue=(key,val)=>{
        const savefiles={...this.state.savefiles};
        savefiles[key]=val;
        this.setState({savefiles:savefiles});        
    }

    setmode=(val)=>{
        this.setState({mode:val});
    }
    savefile=(val)=>{
        this.setState({issaved:true});  
        this.setstatus("Saving...");
        const data={
            name:val,
            content:this.state.editor.getValue(),
            status:"saved",
            mode:this.state.mode
        }
        const savefiles ={...this.state.savefiles};
        const id =Date.now();
        this.setid(id);
        savefiles[id]=data;
        this.setState({savefiles:savefiles});
        setTimeout(() => {
            this.setstatus(data.name);
        }, 1000);
        this.updaterecent();
        this.state.editor.on('change',(cm)=>{
            if(this.state.dropdownvalue===data.mode){
                this.setstatus("Saving...");
                data.content=cm.getValue();
                this.updatevalue(id,data);
                setTimeout(() => {
                    this.setstatus(data.name);
                }, 1000);

            }
        });
    }
    seteditor=(val)=>{
        this.setState({editor:val});
    }
    settheme=(val)=>{
        this.state.editor.setOption("theme",val);
    }
    settab=(val)=>{
        this.state.editor.setOption("indentUnit",val);
    }
    setsize=(val)=>{
        this.state.editor.getWrapperElement().style["font-size"]= val+"px";
        this.state.editor.refresh();
    }
    toggle=()=>{
        this.setState({isopen:!this.state.isopen});   
    }
    setstatus=(val)=>{
        // console.log(val);
        this.setState({status:val});
    }
    render(){
        return(
            <div className="container-fluid" style={{paddingLeft:"0px",paddingRight:"0px"}}>
                <div className="main-wrapper">
                    <NavBar settheme={this.settheme} settab={this.settab} setsize={this.setsize} isopen={this.state.isopen} savefile={this.savefile} toggle={this.toggle}/>
                    <div className="gap"></div>
                    <div className="" style={{marginLeft:"20px",marginRight:"20px"}}>
                        <Row>
                            <Leftcomponent setsaved={this.setsaved} editor={this.state.editor} seteditor={this.seteditor} toggle={this.toggle} setmode={this.setmode} setdropdown={this.setdropdown} dropdownvalue={this.state.dropdownvalue} issaved={this.state.issaved} status={this.state.status} setstatus={this.setstatus} path={this.props.match.url} id={this.state.id}/>
                            <Rightcomponent recent={this.state.recent}/>
                        </Row>
                    </div>
                    <br></br>
                    <Footer/>
                </div>
            </div>
        );
    }
}

export default App;
