import React from "react";
import {Row, Col, Button } from 'reactstrap';
class CustomInputs extends React.Component{
    box=React.createRef();
    state={
        isopen:false
    }
    handleclick=(event)=>{
        // if(!this.state.isopen){
        //     this.box.style.height="0px";
        // }
        // else{
        //     this.box.style.height="100%";
        // }
    }   

    render(){
        return(
            <Row>
                <Row>
                    <Col lg="6" md="6" sm="12" xs="12">
                        <Button color="primary" size="lg" block onClick={this.handleclick}>Inputs <i class="fas fa-pen"></i></Button>
                    </Col>
                    <Col lg="6" md="6" sm="12" xs="12">
                        <Button color="primary" size="lg" block>Run <i class="fas fa-play"></i></Button>
                    </Col>
                </Row>
                <Row>
                    <div className="box" ref={this.box} >
                        <Col lg="6" md="6"sm="12" xs="12">
                            <textarea className="input" style={{width:"100%",height:"150px"}}>Input goes here..</textarea>
                        </Col>
                        <Col lg="6" md="6"sm="12" xs="12">
                            <textarea className="output" style={{width:"100%",height:"150px"}}>Output goes here..</textarea>
                        </Col>
                    </div>
                </Row>
            </Row>  
        );
    }
}
export default CustomInputs;
